import { getLinkPath as getUtilsLinkPath } from "../../../utils/navigationUtils";
import { DISABLED_LINKS } from "../../../constants/navigation";

const MegaMenu = ({ data, activeSub, setActiveSub, onItemClick, categoryPath, categoryName }) => {
