        >
            <div className="grid grid-cols-[330px_1fr] h-[500px]">
                {/* Left Sidebar Category List */}
                <div className="bg-white p-6 border-r border-slate-100 overflow-y-auto h-full custom-scrollbar">
                    {data.left.map(sub => (
                        <div key={sub}
                            onMouseEnter={() => setActiveSub(sub)}
