                                        <img src={hero.testimonial.avatar} alt={hero.testimonial.author} className="w-10 h-10 rounded-full bg-slate-100" />
                                        <div>
                                            <h4 className="text-[15px] font-bold text-[#072b47]">{hero.testimonial.author}</h4>
                                        </div>
